mapper.select:
select
            ID id,
            PARENT_ID parentId,
            NAME name,
            HREF href,
            IS_VALID isValid,
            REMARK remark,
            SEQ seq,
            IS_DELETED isDeleted,
            CREATED_BY createdBy,
            CREATED_NAME createdName,
            DATE_FORMAT(CREATED_AT,'%Y-%m-%d %H:%i:%s') createdAt,
            UPDATED_BY updatedBy,
            UPDATED_NAME updatedName,
            DATE_FORMAT(UPDATED_AT,'%Y-%m-%d %H:%i:%s') updatedAt,
            VERSION version
from t_system_menu t
where t.IS_DELETED = 0
    and t.ID = #{params.id}
    and t.PARENT_ID = #{params.parentId}
    and t.NAME = #{params.name}
    and t.HREF = #{params.href}
    and t.IS_VALID = #{params.isValid}
    and t.REMARK = #{params.remark}
    and t.SEQ = #{params.seq}
    and t.IS_DELETED = #{params.isDeleted}
    and t.CREATED_BY = #{params.createdBy}
    and t.CREATED_NAME = #{params.createdName}
    and t.CREATED_AT = #{params.createdAt}
    and t.UPDATED_BY = #{params.updatedBy}
    and t.UPDATED_NAME = #{params.updatedName}
    and t.UPDATED_AT = #{params.updatedAt}
    and t.VERSION = #{params.version}
     and DATE_FORMAT(CREATED_AT,'%Y-%m-%d') >=  DATE_FORMAT(#{params.createdAtBegin},'%Y-%m-%d') 
     and DATE_FORMAT(CREATED_AT,'%Y-%m-%d') < DATE_FORMAT(DATE_ADD(#{params.createdAtEnd},INTERVAL 1 DAY),'%Y-%m-%d') 
     and DATE_FORMAT(UPDATED_AT,'%Y-%m-%d') >=  DATE_FORMAT(#{params.updatedAtBegin},'%Y-%m-%d') 
     and DATE_FORMAT(UPDATED_AT,'%Y-%m-%d') < DATE_FORMAT(DATE_ADD(#{params.updatedAtEnd},INTERVAL 1 DAY),'%Y-%m-%d')
